/**
 * A helper to track block hash usage
 */
export class BlockUsage {
    #usages = {};
    use(blockHash) {
        this.#usages[blockHash] = (this.#usages[blockHash] || 0) + 1;
    }
    release(blockHash) {
        if (!this.#usages[blockHash])
            return;
        this.#usages[blockHash] -= 1;
    }
    usage(blockHash) {
        return this.#usages[blockHash] || 0;
    }
    clear() {
        this.#usages = {};
    }
}
