import * as $ from '@dedot/shape';
import { isU8a, toHex, u8aToHex } from '@dedot/utils';
export const $Text = $.str;
export const $StorageKey = $.RawHex;
export const $PrefixedStorageKey = $.RawHex;
export const $StorageData = $.RawHex;
export const $RawBytes = $.transform({
    $base: $.RawHex,
    encode: (value) => (isU8a(value) ? u8aToHex(value) : value),
    decode: (value) => value,
});
export const $Bytes = $.transform({
    $base: $.PrefixedHex,
    encode: (value) => toHex(value),
    decode: (value) => value,
});
