import * as $ from '@dedot/shape';
import { HexString } from '@dedot/utils';
export type OpaqueExtrinsicLike = HexString | string | Uint8Array;
export type OpaqueExtrinsic = HexString;
export declare const $OpaqueExtrinsic: $.Shape<OpaqueExtrinsicLike, `0x${string}`>;
export declare const $UncheckedExtrinsic: $.Shape<OpaqueExtrinsicLike, `0x${string}`>;
