export const UNKNOWN_ERROR_CODE = -99999;
export class JsonRpcError extends Error {
    code;
    data;
    constructor(eOrMsg, code = UNKNOWN_ERROR_CODE, data) {
        if (typeof eOrMsg === 'string') {
            super(`${code}: ${eOrMsg}`);
            this.code = code;
            this.data = data;
        }
        else {
            const { message, code, data } = eOrMsg;
            super(`${code}: ${message}`);
            this.code = code;
            this.data = data;
        }
    }
}
