export function assert(condition, message) {
    if (condition) {
        return;
    }
    throwError(message);
}
/**
 * Throw out error if condition is undefined, false, null, '' or 0
 *
 * @param condition
 * @param message
 */
export function assertFalse(condition, message) {
    assert(!condition, message);
}
function throwError(message) {
    if (message instanceof Error) {
        throw message;
    }
    throw new Error(message);
}
/**
 * Ensure presence for value, else throw an error!
 * @param value
 * @param message
 */
export function ensurePresence(value, message) {
    return value ?? throwError(message || 'Value is not present (null or undefined)');
}
