import { AnyShape, Input, Output, Shape } from "../common/mod.js";
export type InputTuple<T extends AnyShape[]> = {
    readonly [K in keyof T]: Input<T[K]>;
};
export type OutputTuple<T extends AnyShape[]> = {
    [K in keyof T]: Output<T[K]>;
};
export declare function tuple<T extends AnyShape[]>(...shapes: [...T]): Shape<InputTuple<T>, OutputTuple<T>>;
