import { useState } from 'react';
export const useListenerCounter = (event) => {
    const [counter, setCounter] = useState(0);
    const tryIncrease = (eventToCheck) => {
        if (event !== eventToCheck)
            return;
        setCounter((counter) => counter + 1);
    };
    const tryDecrease = (eventToCheck) => {
        if (event !== eventToCheck)
            return;
        setCounter((counter) => counter - 1);
    };
    return {
        tryIncrease,
        tryDecrease,
        counter,
        hasAny: counter > 0,
    };
};
